client_script:
frappe.web_form.after_load = (frm) => {
    // init script here
    //console.log("hello")
    $('div.web-form-actions').hide();
    frappe.web_form.set_value('status', 'Draft');

    frappe.call({
        method:"get_allcounties",

        callback: function(r) {
            if (r.message) {
                var options = [];
                $.each(r.message, function(i,d) {
                    //console.log(d);
                    options.push(d.county_name)

                });
            //	console.log(options);
                frappe.web_form.set_df_property('county_name', 'options', options);
            //	frm.fields_dict.county.options = options;
            }
        }

    });

}


frappe.web_form.on('county_name', (field, value) => {
    filterConstituencies(value);
})

function filterConstituencies(county_name) {
    var myurl = 'api/resource/Constituencies?fields=["county","constituency_code","constituency_name"]&filters=[["county","=","'+ county_name +'"]]'
	$.ajax({
		type: 'GET', 
		url: myurl,
    success: function(result) {

        var options = []
        $.each(result.data, function(i,d) {
    //      //  console.log(d)   
	        options.push(d.constituency_name)
        })
    //    console.log(options)

        frappe.web_form.set_df_property('constituency_name', 'options', options);

    }
    })
}

frappe.web_form.on('constituency_name', (field, value) => {
    filterWard(value);
})

function filterWard(constituency_name) {
    var myurl = 'api/resource/Wards?fields=["caw_code","ward_name"]&filters=[["constituency_name","=","'+ constituency_name +'"]]'
	$.ajax({
		type: 'GET', 
		url: myurl,
    success: function(result) {

        var options = []
        $.each(result.data, function(i,d) {
    //        console.log(d)   
	        options.push(d.ward_name)
        })
    //    console.log(options)

        frappe.web_form.set_df_property('ward_name', 'options', options);

    }
    })
}

frappe.web_form.on('mobile_no', (field, value) => {
    var phoneno = /^\d{12}$/;
    if (!(value.match(phoneno))) {
        frappe.msgprint('Please enter correct phone no.e.g 254712345678');
        //field.set_value(0);
    }
});

frappe.web_form.on('email_id', (field, value) => {
    if (value && (!(frappe.utils.validate_type(value), "email"))) {
        frappe.throw('Invalid email address');
    }
});



var code_num = Math.floor(Math.random() * 90000) + 10000;
let d = new frappe.ui.Dialog({
    title: 'Enter Verication Code',
    fields: [
        {
            label: 'Verification Code',
            fieldname: 'verifications_code',
            fieldtype: 'Data'
        }
    ],
    primary_action_label: 'Verify Member',
    primary_action(values) {
        if ((values.verifications_code) && (values.verifications_code == code_num)){
            frappe.web_form.set_value('status', 'Verified');
            frappe.show_alert('Member verified. Your member account details will be sent to you shortly.', 1);
            frappe.web_form.set_value('status', 'Verified');
            d.hide();
            return true;

        }else{
            frappe.throw('Wrong verification code. Please enter the correct code');
            d.show();

        }


    }
});



