    let data = frappe.web_form.get_values();
    console.log(data);

    if (data.member_name && data.other_names && data.id_passport && data.mobile_no && data.county_name && (data.status == 'Draft')){
       ;

        var message = 'verification Code:' + code_num;            

        frappe.call({
                method: "frappe.core.doctype.sms_settings.sms_settings.send_sms",
                args: {
                    receiver_list: [data.mobile_no],
