                },
                callback: function(r) {


                    if (r.exc) {
                        frappe.throw(d.show());  
                      //  frappe.show_alert(r.exc, 2);

                        return;
                    } 
                }
        });  
        frappe.throw(d.show());  



        }   

    return true;

}
